import * as React from 'react'
import { Text, StyleSheet } from 'react-native'
import { type MessageStatus as MessageStatusType } from './types'

const statusIcons: Record<string, string> = {
  sending: '◌',
  sent: '✓',
  delivered: '✓✓',
  read: '✓✓',
  failed: '✗',
}

const statusColors: Record<string, string> = {
  sending: '#6b7280', // --color-message-sending
  sent: '#2563eb',    // --color-message-sent
  delivered: '#1d4ed8', // --color-message-delivered
  read: '#16a34a',    // --color-message-read
  failed: '#dc2626',  // --color-message-failed
}

export function MessageStatus({ status }: { status: MessageStatusType }) {
  return (
    <Text 
      style={[styles.status, { color: statusColors[status] }]}
      accessibilityLabel={`Message ${status}`}
    >
      {statusIcons[status]}
    </Text>
  )
}

const styles = StyleSheet.create({
  status: {
    fontSize: 12,
  },
})
